const Sequelize = require('sequelize')
const config = require('config')
const fs = require('fs')
const _ = require('lodash')
const path = require('path')

const sequelize = new Sequelize(config.database.schemaName, config.database.username, config.database.password, {
    host: 'localhost',
    dialect: 'mysql',
})

const modelFiles = fs.readdirSync(__dirname + '/models/')

/*
 * export each model in ./models
 */
_.each(modelFiles, (file) => {
    const filename = path.parse(file).name
    exports[filename] = sequelize.import(__dirname + '/models/' + filename)
})

sequelize.sync()
