module.exports = (sequelize, DataTypes) => {
    return sequelize.define('post', {
        id: {
            type: DataTypes.INTEGER,
            autoIncrement: true,
            primaryKey: true
        },
        title: DataTypes.STRING,
        content: 'LONGTEXT' // due to limit of 64kb on DataTypes.TEXT
    }, {
        timestamps: true,
        paranoid: true,
        underscored: true
    })
}
