module.exports = {
    handleRestError: (req, res) => {
        return (err) => {
            switch(err.name) {
                case 'TypeError':
                case 'RangeError':
                    res.status(400)
                    break
                case 'InternalError':
                default:
                    res.status(500)
            }
            res.send(err.message)
        }
    }
}
