const db = require('db')

module.exports = {
    getAllPosts: (req, res) => {
        db.Post.findAll()
        .then((posts) => {
            res.json(posts)
        })
    },
    getLatestPost: (req, res) => {
        db.Post.findOne({ order:'`created_at` DESC'})
        .then((post) => {
            res.json(post)
        })
    },
    getPostById: (req, res) => {
        db.Post.findById(req.params.id)
        .then((post) => {
            res.json(post)
        })
    },
    createPost: (req, res) => {
        db.Post.create({
            title: req.body.title,
            content: req.body.content
        })
        .then((post) => {
            res.send(post)
        })
    }
}
