const config = require('config')

module.exports = {
    // authentification used for creating a new post
    // simply checks if the password sent matches the one stored in environnement variable
    adminAuthentification: (req, res, next) => {
        if (req.body.password === config.adminPassword) {
            next()
        } else {
            res.status(403).send('Forbidden access')
        }
    }
}
